mapper.select:
select
from TB_SUBJECT
where COL_SUBJECT_DB_ID =#{subjectDbId} AND COL_SUBJECT_TYPE = #{subjectType} order by rand() LIMIT #{count}

SELECT T.ID AS id, DB.COL_SUBJECT_DB_NAME AS subjectDbName ,T.COL_SUBJECT_CONTENT AS subjectContent, T.COL_SUBJECT_DB_ID AS subjectDbId, T.COL_SUBJECT_TYPE AS subjectType, T.COL_DEFFICULTY_WEIGHT AS defficultyWeight, T.COL_IS_OPEN AS isOpen, T.COL_CREATE_TIME AS createTime, T.COL_UPDATE_TIME AS updateTime, T.COL_CREATOR_ID AS creatorId, T.COL_UPDATER_ID AS updaterId FROM TB_SUBJECT T, TB_SUBJECT_DB DB
T.COL_SUBJECT_DB_ID= DB.ID
AND T.COL_SUBJECT_CONTENT like "%"#{subjectContent}"%"
AND T.COL_SUBJECT_DB_ID =#{subjectDbId}
AND T.COL_SUBJECT_TYPE = #{subjectType}
AND T.COL_IS_OPEN =#{isOpen}
